options.customCss:
:root {
  /* Agriculture-inspired Colors - More Contrast for Better Readability */
  --chat--color-primary: #15803d;
  --chat--color-primary-shade-50: #166534;
  --chat--color-primary-shade-100: #14532d;
  --chat--color-secondary: #d97706;
  --chat--color-secondary-shade-50: #b45309;
  --chat--color-secondary-shade-100: #92400e;
  --chat--color-accent: #dc2626;
  --chat--color-accent-shade-50: #b91c1c;
  --chat--color-accent-shade-100: #991b1b;
  --chat--color-white: #ffffff;
  --chat--color-light: #f7fee7;
  --chat--color-light-shade-50: #ecfccb;
  --chat--color-light-shade-100: #d9f99d;
  --chat--color-medium: #65a30d;
  --chat--color-dark: #1a5d1a;
  --chat--color-disabled: #9ca3af;
  --chat--color-typing: #86efac;
  --chat--color-soil: #8b4513;
  --chat--color-sky: #0ea5e9;
  --chat--color-sun: #fbbf24;

  /* Enhanced Layout - Better for Farmers */
  --chat--spacing: 1.25rem;
  --chat--border-radius: 1.25rem;
  --chat--transition-duration: 0.3s;
  --chat--font-family: (
    'Inter',
    'Roboto',
    'Segoe UI',
    'Helvetica Neue',
    'Arial',
    'sans-serif'
  );

  /* Window Dimensions - Much More Compact */
  --chat--window--width: 320px;
  --chat--window--height: 450px;
  --chat--window--bottom: var(--chat--spacing);
  --chat--window--right: var(--chat--spacing);
  --chat--window--z-index: 9999;
  --chat--window--border: 1px solid var(--chat--color-primary);
  --chat--window--border-radius: 1rem;
  --chat--window--margin-bottom: var(--chat--spacing);
  --chat--window--box-shadow: 
    0 15px 30px -8px rgba(21, 128, 61, 0.2),
    0 0 0 1px rgba(21, 128, 61, 0.08),
    inset 0 1px 0 rgba(255, 255, 255, 0.1);

  /* Header Styles - Compact and Proportional */
  --chat--header-height: auto;
  --chat--header--padding: 0.75rem 1rem;
  --chat--header--background: linear-gradient(135deg, #14532d 0%, #15803d 50%, #16a34a 100%);
  --chat--header--color: var(--chat--color-white);
  --chat--header--border-top: none;
  --chat--header--border-bottom: 1px solid rgba(255, 255, 255, 0.3);
  --chat--header--border-left: none;
  --chat--header--border-right: none;
  --chat--heading--font-size: 1.25rem;
  --chat--subtitle--font-size: 0.75rem;
  --chat--subtitle--line-height: 1.2;
  --chat--subtitle--margin-top: 0.125rem;

  /* Message Styles - Compact and Readable */
  --chat--message--font-size: 0.8rem;
  --chat--message--padding: 0.625rem 1rem;
  --chat--message--border-radius: 1rem;
  --chat--message-line-height: 1.4;
  --chat--message--margin-bottom: 0.625rem;
  --chat--message--bot--background: linear-gradient(135deg, #f7fee7 0%, #ecfccb 100%);
  --chat--message--bot--color: #1a5d1a;
  --chat--message--bot--border: 1px solid #d9f99d;
  --chat--message--bot--box-shadow: 
    0 4px 15px -3px rgba(21, 128, 61, 0.15),
    0 2px 4px -1px rgba(0, 0, 0, 0.08);
  --chat--message--user--background: linear-gradient(135deg, #15803d 0%, #166534 100%);
  --chat--message--user--color: #ffffff;
  --chat--message--user--border: none;
  --chat--message--user--box-shadow: 
    0 4px 15px -3px rgba(21, 128, 61, 0.3),
    0 2px 4px -1px rgba(0, 0, 0, 0.08);
  --chat--message--pre--background: rgba(21, 128, 61, 0.1);
  --chat--messages-list--padding: 0.75rem;

  /* Toggle Button - Much More Compact */
  --chat--toggle--size: 50px;
  --chat--toggle--width: var(--chat--toggle--size);
  --chat--toggle--height: var(--chat--toggle--size);
  --chat--toggle--border-radius: 50%;
  --chat--toggle--background: linear-gradient(135deg, #15803d 0%, #166534 100%);
  --chat--toggle--hover--background: linear-gradient(135deg, #166534 0%, #14532d 100%);
  --chat--toggle--active--background: linear-gradient(135deg, #14532d 0%, #1a5d1a 100%);
  --chat--toggle--color: var(--chat--color-white);
  --chat--toggle--box-shadow: 
    0 15px 30px -8px rgba(21, 128, 61, 0.3),
    0 8px 8px -4px rgba(0, 0, 0, 0.08),
    0 0 0 1px rgba(255, 255, 255, 0.1);
  --chat--toggle--hover--box-shadow: 
    0 20px 40px -10px rgba(21, 128, 61, 0.4),
    0 10px 10px -5px rgba(0, 0, 0, 0.1),
    0 0 0 1px rgba(255, 255, 255, 0.15);

  /* Input Area - Compact and Accessible */
  --chat--textarea--height: 40px;
  --chat--textarea--max-height: 100px;
  --chat--input--font-size: 0.8rem;
  --chat--input--border: 1px solid #d9f99d;
  --chat--input--border-radius: 0.75rem;
  --chat--input--padding: 0.5rem 0.75rem;
  --chat--input--background: linear-gradient(135deg, #f7fee7 0%, #ecfccb 100%);
  --chat--input--text-color: #1a5d1a;
  --chat--input--line-height: 1.3;
  --chat--input--placeholder--font-size: var(--chat--input--font-size);
  --chat--input--border-active: 1px solid #15803d;
  --chat--input--left--panel--width: 1.5rem;
  --chat--input--box-shadow: 
    0 2px 4px -1px rgba(0, 0, 0, 0.08),
    0 1px 2px -1px rgba(0, 0, 0, 0.04);

  /* Button Styles - Compact and Clickable */
  --chat--button--color: var(--chat--color-white);
  --chat--button--background: linear-gradient(135deg, #15803d 0%, #166534 100%);
  --chat--button--padding: 0.625rem 1.25rem;
  --chat--button--border-radius: 0.75rem;
  --chat--button--hover--color: var(--chat--color-white);
  --chat--button--hover--background: linear-gradient(135deg, #166534 0%, #14532d 100%);
  --chat--close--button--color-hover: #dc2626;
  --chat--button--box-shadow: 
    0 3px 4px -1px rgba(21, 128, 61, 0.25),
    0 1px 3px -1px rgba(0, 0, 0, 0.08);

  /* Send and File Buttons - Compact */
  --chat--input--send--button--background: linear-gradient(135deg, #15803d 0%, #166534 100%);
  --chat--input--send--button--color: var(--chat--color-white);
  --chat--input--send--button--background-hover: linear-gradient(135deg, #166534 0%, #14532d 100%);
  --chat--input--send--button--color-hover: var(--chat--color-white);
  --chat--input--file--button--background: linear-gradient(135deg, #d97706 0%, #b45309 100%);
  --chat--input--file--button--color: #ffffff;
  --chat--input--file--button--background-hover: linear-gradient(135deg, #b45309 0%, #92400e 100%);
  --chat--input--file--button--color-hover: #ffffff;
  --chat--files-spacing: 0.5rem;

  /* Body and Footer - Better Contrast */
  --chat--body--background: linear-gradient(135deg, #f7fee7 0%, #ecfccb 50%, #d9f99d 100%);
  --chat--footer--background: linear-gradient(135deg, #14532d 0%, #15803d 100%);
  --chat--footer--color: #ffffff;
  --chat--footer--border-top: 3px solid rgba(255, 255, 255, 0.3);
}


/* Enhanced message styling for farmers */
.chat-message {
  max-width: 90%;
  margin-left: auto;
  margin-right: auto;
  text-align: left;
  position: relative;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  border: 3px solid transparent;
  font-weight: 400;
  font-family: 'Inter', 'Roboto', 'Segoe UI', 'Helvetica Neue', 'Arial', sans-serif;
  letter-spacing: 0.01em;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

.chat-message:hover {
  transform: translateY(-2px) scale(1.02);
  box-shadow: 
    0 20px 25px -5px rgba(21, 128, 61, 0.3),
    0 10px 10px -5px rgba(0, 0, 0, 0.1);
}

/* Bot message with much more compact styling */
.chat-message-bot {
  margin-right: auto;
  margin-left: 0;
  border-bottom-left-radius: 0.5rem;
  position: relative;
  background: linear-gradient(135deg, #f7fee7 0%, #ecfccb 100%) !important;
  color: #1a5d1a !important;
  border: 1px solid #d9f99d !important;
  box-shadow: 
    0 4px 15px -3px rgba(21, 128, 61, 0.15),
    0 2px 4px -1px rgba(0, 0, 0, 0.08) !important;
  padding: 0.625rem 1rem !important;
  border-radius: 1rem !important;
  max-width: 90% !important;
  margin-bottom: 0.625rem !important;
  font-size: 0.8rem !important;
  line-height: 1.4 !important;
  display: block !important;
  opacity: 1 !important;
  visibility: visible !important;
}

