/* User message with larger tractor decoration */
.chat-message-user {
  margin-left: auto;
  margin-right: 0;
  border-bottom-right-radius: 0.75rem;
  position: relative;
  background: linear-gradient(135deg, #15803d 0%, #166534 100%) !important;
  color: #ffffff !important;
  border: 3px solid #15803d !important;
  box-shadow: 
