  --chat--body--background: linear-gradient(135deg, #374151 0%, #4b5563 50%, #6b7280 100%);
  --chat--footer--background: linear-gradient(135deg, #065f46 0%, #047857 100%);
  --chat--footer--color: #f9fafb;
  --chat--footer--border-top: 3px solid rgba(255, 255, 255, 0.2);

