    0 0 0 2px rgba(255, 255, 255, 0.1);

  --chat--input--send--button--background: linear-gradient(135deg, #059669 0%, #047857 100%);
  --chat--input--send--button--background-hover: linear-gradient(135deg, #047857 0%, #065f46 100%);
  --chat--input--file--button--background: linear-gradient(135deg, #f59e0b 0%, #d97706 100%);
  --chat--input--file--button--background-hover: linear-gradient(135deg, #d97706 0%, #b45309 100%);
}

/* Dark mode message styling */
.chat-window.dark-mode .chat-message-bot {
  background: linear-gradient(135deg, #374151 0%, #4b5563 100%) !important;
  color: #f9fafb !important;
  border: 3px solid #6b7280 !important;
  box-shadow: 
    0 10px 30px -5px rgba(5, 150, 105, 0.3),
    0 6px 8px -1px rgba(0, 0, 0, 0.2) !important;
